use serde::{Deserialize, Serialize};
use super::BasicNews;
use super::{News, Area, AreaType};

#[derive(Deserialize, Serialize, Debug)]
pub enum RouteType {
    Unknown(u16)
}

impl From<u16> for RouteType {
    fn from(v: u16) -> RouteType {
        match v {
            _ => RouteType::Unknown(v)
        }
    }
}

#[derive(Deserialize, Debug)]
pub(crate) struct BasicRoute {
    #[serde(alias = "areaId")]
    pub(crate) area_id: u16,
    // pub(crate) news: Vec<BasicNews>,
    #[serde(alias = "routeColor")]
    pub(crate) route_color: Option<String>,
    #[serde(alias = "routeId")]
    pub(crate) route_id: u16,
    #[serde(alias = "routeLongName")]
    pub(crate) route_long_name: String,
    #[serde(alias = "routeShortName")]
    pub(crate) route_short_name: String,
    #[serde(alias = "routeType")]
    pub(crate) route_type: u16,
    #[serde(alias = "type")]
    pub(crate) ty: AreaType,
}

#[derive(Serialize, Debug)]
pub struct Route {
    area: Area,
    // news: Vec<News>,
    pub(crate) route_color: Option<String>,
    pub(crate) route_id: u16,
    pub(crate) route_long_name: String,
    pub(crate) route_short_name: String,
    pub(crate) route_type: RouteType,
    pub(crate) area_type: AreaType,
}


impl Route {
    pub(crate) fn from_basic(mut br: BasicRoute, a: Area) -> Route {
        Route {
            area: a,
            // news,
            route_color: br.route_color,
            route_id: br.route_id,
            route_long_name: br.route_long_name,
            route_short_name: br.route_short_name,
            route_type: RouteType::from(br.route_type),
            area_type: br.ty
        }
    }
}

